import React, { useState } from "react";
import { Button, Icon } from 'semantic-ui-react'
import BtnEl from "../BotonEL/BtnEl";
import Eliminar from "../Eliminar/Eliminar";
import "./Tabla.css";
import { getDatabase, ref, onValue, get, child } from "firebase/database";

class Tabla extends React.Component{

    constructor(props){
        super(props);
        this.state =({
            MisReservas:([])
        })
    }

    componentDidMount() {

        //const db = getDatabase();
        //const r = ref(db, 'proyectofinalweb-2c536-default-rtdb');

          const dbRef = ref(getDatabase());
            get(dbRef, "proyectofinalweb-2c536-default-rtdb").then((snapshot) => {
            let MiReserva = ([]);
            if (snapshot.exists()) {
                console.log(snapshot.val());
                snapshot.forEach(snap => {
                    //MisReservas.push(snap.val())
                    MiReserva = snap.val();
                    this.handleUpdate(MiReserva);
                      
                });
            } else {
                console.log("No data available");
            }
            }).catch((error) => {
            console.error(error);
            });
      
    }

    handleUpdate(Mr){
        this.setState({ MisReservas: Mr });
    }
    
    render() {
        return(
        <div className="TbContainer">
            <table className="Tabla">
                <tr className="TrHeader">
                    <th>Fecha</th>
                    <th>Hora</th>
                    <th>Descripci√≥n</th>
                    <th>Opciones</th>
                </tr>
                <tr className="TrBody">
                    <td>Soleado</td>
                    <td>Mayormente soleado</td>
                    <td>Parcialmente nublado</td>
                    <td>
                        <BtnEl></BtnEl>
                    </td>
                </tr>
            </table>
        </div>
        );
    }
}

export default Tabla;
