import React, { useState } from "react";
import { Button, Icon } from 'semantic-ui-react'
import Eliminar from "../Eliminar/Eliminar";


const BtnEl = function() {

    const [isOpenE, setIsOpenE] = useState(true);

    const openE = () =>{
        setIsOpenE(true);
    }

    const CloseE = () =>{
        setIsOpenE(false);
    }
    
        return(
            <div>
                <Button color="green" animated='vertical'>
                    <Button.Content hidden>Editar</Button.Content>
                        <Button.Content visible>
                        <Icon name='pencil' />
                    </Button.Content>
                </Button>

                <Button color="red" animated='vertical' onClick={openE}>
                    <Button.Content hidden>Eliminar</Button.Content>
                        <Button.Content visible>
                        <Icon name='trash' />
                    </Button.Content>
                </Button>

                {isOpenE && <Eliminar 
                    isOpenE={isOpenE} 
                    closeE={CloseE}
                />}
            </div>
        );
}

export default BtnEl;
