import React, { useState } from "react";
import { Button, Icon } from 'semantic-ui-react'
import Eliminar from "../Eliminar/Eliminar";
import "./Tabla.css";

class Tabla extends React.Component{

    constructor(){
        super();
        this.state ={
            isOpenModal: false,
            setIsOpenM: false
        };
    }
    
    openE = () =>{
        const{isOpenModal, setIsOpenM} = this.state

        this.setState ({
            isOpenModal: true,
            setIsOpenM: true
        });

    }

    CloseE = () =>{
        const{isOpenModal, setIsOpenM} = this.state

        this.setState ({
            isOpenModal: false,
            setIsOpenM: false
        });
    }
    
    render() {
        const{isOpenModal} = this.state
        return(
        <div className="TbContainer">
            <table className="Tabla">
                <tr className="TrHeader">
                    <th>Fecha</th>
                    <th>Hora</th>
                    <th>Descripci√≥n</th>
                    <th>Opciones</th>
                </tr>
                <tr className="TrBody">
                    <td>Soleado</td>
                    <td>Mayormente soleado</td>
                    <td>Parcialmente nublado</td>
                    <td>

                    <Button color="green" animated='vertical'>
                        <Button.Content hidden>Editar</Button.Content>
                            <Button.Content visible>
                            <Icon name='pencil' />
                        </Button.Content>
                    </Button>

                    <Button color="red" animated='vertical' onClick={this.openE}>
                        <Button.Content hidden>Eliminar</Button.Content>
                            <Button.Content visible>
                            <Icon name='trash' />
                        </Button.Content>
                    </Button>
                    {isOpenModal && <Eliminar 
                        isOpenE={isOpenModal} 
                        closeE={this.CloseE}
                    />}
                    </td>
                </tr>
            </table>
        </div>
        );
    }
}

export default Tabla;
